import { Link, useParams } from "react-router-dom";
import Comments from "../components/Comments";
import Layout from "../components/layout/Layout";
import axios from "axios";
import React, { useEffect, useState } from "react";
import DisplayData from "./Author/dangerouslySetInnerHTML";
import LikeBlog from "../components/LikeBlog";

const BlogPage = () => {
  var { id } = useParams();
  const [blog, setBlog] = useState({ comment: [] }); // Initialize blog state as an object with a comment property that is an array
  const [comments, setComments] = useState("");
  const [likes, setLikes] = useState(0);
  const [name, setName] = useState("");
  const [comment, setComment] = useState("");
  const [blogId, setBlogId] = useState(0);

  const addComment = (comment) => {
    setBlog((prevBlog) => {
      return { ...prevBlog, comment: [...prevBlog.comment, comment] };
    });
    setComments(comment);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      name: name,
      comment: comment,
    };
    axios
      .post(
        `http://localhost:8080/api/blog/get-blog/add-comment/${blogId}`,
        data
      )
      .then((res) => {
        addComment(res.data.comment);
      });

    setName("");
    setComment("");
  };

  const handleLike = () => {
    axios
      .post(`http://localhost:8080/api/blog/get-blog/like/${id}`)
      .then((res) => {
        setLikes(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    async function fetchData() {
      await axios
        .get(`http://localhost:8080/api/blog/get-blog/${id}`)
        .then((res) => {
          setBlog(res.data.blog);
        });
    }
    fetchData();
  }, [likes, comments, id]);

  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-9 mx-auto bg-light">
            <h1 className="mt-5">
              <DisplayData data={blog.title} />
            </h1>
            <p className="mb-5">
              {blog.createdAt} by
              <Link to="/" style={{ color: "gray" }}>
                Mark
              </Link>
            </p>
            <div
              id="content-main"
              className="left relative"
              style={{ height: "auto !important" }}
            >
              <div>
                <img
                  src={`/${blog?.path}`}
                  className="img-fluid"
                  alt="Blog Banner"
                />
              </div>

              <div>
                <DisplayData data={blog.content} />
              </div>
              <div>
                <LikeBlog handleLike={handleLike} proplikes={blog.likes} />
              </div>
            </div>

            {/* =============================================================== */}
            <div className="fs-2 my-3">
              Comments ( {blog && blog.comment.length} )
            </div>

            {blog.comment &&
              blog.comment.map((comment, index) => {
                const date = new Date(comment?.createdAt);
                const options = {
                  weekday: "long",
                  year: "numeric",
                  month: "long",
                  day: "numeric",
                };
                const formattedDate = date.toLocaleDateString("en-US", options);
                setBlogId(blog._id);

                return (
                  <div className="card mt-3 col-md-9 mx-auto" key={index}>
                    <div className="card-header">
                      <img
                        style={{
                          width: "50px",
                          height: "50px",
                          borderRadius: "50%",
                        }}
                        src={`/${blog?.path}`}
                        className="me-4 img-fluid"
                        alt="User Profile"
                      />

                      {comment?.username}
                    </div>
                    <div className="card-body mb-0">
                      <blockquote className="blockquote mb-0">
                        <p>{comment?.comment}</p>
                        <footer className="blockquote-footer">
                          {formattedDate}
                        </footer>
                      </blockquote>
                      <div className="mb-0">
                        <button
                          // onClick={() => handleReply(comment._id)}
                          className="btn btn-link btn-sm"
                        >
                          Reply
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            <div className="col-md-10  mx-auto">
              <Comments
                name={name}
                comment={comment}
                handleSubmit={handleSubmit}
                setName={setName()}
                setComment={setComment()}
              />
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
