import React from "react";
import { Link } from "react-router-dom";
import DisplayData from "../pages/Author/dangerouslySetInnerHTML";

const Card = ({ posts }) => {
  return (
    <>
      {posts &&
        posts.map((post) => (
          <Link
            to="/blogPage"
            className="nav-link text-dark m-2"
            style={{ textDecoration: "none" }}
            key={post._id}
          >
            <div className="card p-3 m-3">
              <div className="row">
                <div className="col-md-4">
                  <img
                    src={`http://localhost:8080/${post.path}`}
                    className="img-fluid rounded-start"
                    alt="..."
                  />
                </div>
                <div className="col-md-8">
                  <div className="card-body">
                    <h5 className="card-title">
                      <DisplayData data={post.title.substring(0, 70)} />
                      {/* {post.title.substring(0, 50)}... */}
                    </h5>
                    <p className="card-text">
                      <DisplayData data={post.content.substring(0, 200)} />
                      {/* {post.content.substring(0, 200)}... */}
                    </p>

                    <p className="card-text">
                      <small className="text-muted">
                        Last updated 3 mins ago
                      </small>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </Link>
        ))}
    </>
  );
};

export default Card;
