import React, { useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { useNavigate } from "react-router-dom";
// import { useSnackbar } from "notistack";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [answer, setAnswer] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "email":
        setEmail(value);
        break;
      case "newPassword":
        setNewPassword(value);
        break;
      case "answer":
        setAnswer(value);
        break;
      default:
        break;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/api/auth/forget-password", {
        email,
        newPassword,
        answer,
      });
      if (res.data.success === true) {
        toast.success("Reset Password Successfully");
        if (res.data.message) navigate("/login");
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <h1 className="text-center display-4 mt-5">Reset Password </h1>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email</label>
                <input
                  value={email}
                  onChange={handleChange}
                  type="email"
                  className="form-control"
                  name="email"
                  required
                />
              </div>{" "}
              <div className="form-group">
                <label>What is your favourite Book</label>
                <input
                  value={answer}
                  onChange={handleChange}
                  type="text"
                  className="form-control"
                  name="answer"
                  required
                />
              </div>
              <div className="form-group">
                <label>New Password</label>
                <input
                  value={newPassword}
                  onChange={handleChange}
                  type="password"
                  className="form-control"
                  name="newPassword"
                  required
                />
              </div>
              <div className="form-group mt-5">
                <button
                  type="submit"
                  className="btn btn-primary btn-lg btn-block p-2 mt-0"
                >
                  Reset Password
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default ForgotPassword;
