import React, { useState } from "react";
import { toast } from "react-toastify";
import Layout from "../../components/Layout/Layout.js";
import axios from "axios";
import { useNavigate, useLocation, Link } from "react-router-dom";
// import { useSnackbar } from "notistack";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  // const { enqueueSnackbar, closeSnackbar } = useSnackbar();

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "email":
        setEmail(value);
        break;
      case "password":
        setPassword(value);
        break;
      default:
        break;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/api/auth/login", {
        email,
        password,
      });
      if (res.data.success === true) {
        // enqueueSnackbar("I love hooks");
        localStorage.setItem("user", JSON.stringify(res.data.user));
        localStorage.setItem("token", JSON.stringify(res.data.token));
        toast.success("Login Successfully");
        if (res.data.message) navigate(location.state || "/");
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };
  return (
    <Layout title={"Login - E-Commerce"}>
      <div className="container">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <h1 className="text-center display-4 mt-5">Registeration </h1>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email</label>
                <input
                  value={email}
                  onChange={handleChange}
                  type="email"
                  className="form-control"
                  name="email"
                  required
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  value={password}
                  onChange={handleChange}
                  type="password"
                  className="form-control"
                  name="password"
                  required
                />
              </div>
              <div className="form-group mt-5 mx-auto">
                <Link to="/forget-password" className=" p-2 mt-0">
                  Forgot Password
                </Link>
              </div>
              <div className="form-group mt-5">
                <button
                  type="submit"
                  className="btn btn-primary btn-lg btn-block p-2 mt-0"
                >
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Login;
