import { Link, useParams } from "react-router-dom";
import Comments from "../components/Comments";
import Layout from "../components/layout/Layout";
import axios from "axios";
import React, { useEffect, useState } from "react";

const BlogPage = () => {
  var { id } = useParams();
  const [blog, setBlog] = useState("");
  async function fetchData() {
    //  await id = useParams();

    await axios
      .get(`http://localhost:8080/api/blog/get-blog/${id}`)
      .then((res) => {
        console.log(res.data.blog);
        setBlog(res.data.blog);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-9 mx-auto bg-light">
            <h1 className="mt-5">
              {/* How to open an Easypaisa Account (Updated 2023) */}
              {blog.title}
            </h1>
            <p className="mb-5">
              {blog.createdAt} by{" "}
              <Link to="/" style={{ color: "gray" }}>
                Mark
              </Link>
            </p>
            <div
              id="content-main"
              className="left relative"
              style={{ height: "auto !important" }}
            >
              <div>
                <img
                  src={`http://localhost:8080/${blog.path}`}
                  className="img-fluid"
                  alt="Blog Banner"
                />
              </div>

              <div>
                <p>{/* <DisplayData data={post.content} /> */}</p>
              </div>
            </div>

            {/* =============================================================== */}
            <div className="display-1">Comments</div>
            <Comments />
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
