import React, { useEffect, useState } from "react";
import Card from "../components/Card";
import InfiniteScroll from "react-infinite-scroll-component";
import CategoryList from "../components/CategoryList";
import Layout from "../components/layout/Layout";
import axios from "axios";

const ByCategoryPage = () => {
  const [posts, setPosts] = useState([]);
  const [page, setPage] = useState(1);
  const [dataLength, setDataLength] = useState("");
  const [hasMore, setHasMore] = useState(true);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    async function fetchData() {
      await axios
        .get(`http://localhost:8080/api/blog/show-posts?page=${page}`)
        .then((res) => {
          setPosts(res.data.blogs);
          setDataLength(res.data.BlogCount);
          setTotalPages(Math.ceil(res.data.BlogCount / 7));
        });
    }
    fetchData();
  }, [page]);

  async function fetchMorePosts() {
    setPage(page + 1);
    await axios
      .get(`http://localhost:8080/api/blog/show-posts?page=${page + 1}`)
      .then((data) => setPosts([...posts, ...data.blogs]));
  }

  function handlePaginationClick(newPage) {
    setPage(newPage);
  }

  const paginationButtons = [];
  for (let i = 1; i <= totalPages; i++) {
    paginationButtons.push(
      <button
        key={i}
        className={`btn btn-primary mx-2 ${i === page ? "active" : ""}`}
        onClick={() => handlePaginationClick(i)}
      >
        {i}
      </button>
    );
  }

  return (
    <Layout>
      <div className="row">
        {/* Left Panel of Page */}
        <div className="col-md-8 m-3 p-3">
          <h4 className="display-5 mx-2 p-2">Category Posts</h4>
          <hr />
          {/* <InfiniteScroll
            dataLength={dataLength}
            next={fetchMorePosts}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            endMessage={
              <p style={{ textAlign: "center" }}>
                <b>Yay! You have seen it all</b>
              </p>
            }
          > */}
          <Card posts={posts} />
          {/* </InfiniteScroll> */}
          <div className="d-flex justify-content-center mt-4">
            {paginationButtons}
          </div>
        </div>

        {/* Right Panel of Page */}
        <div className="col-md-3 m-3 p-3">
          {/* =====================Small Card=========== */}
          <CategoryList />
        </div>
      </div>
    </Layout>
  );
};

export default ByCategoryPage;
