import React from "react";
import { Link } from "react-router-dom";

const PopularPost = ({ popularPosts }) => {
  // const [posts, setPosts] = useState([]);

  // const fetchData = async () => {
  //   await axios.get("/api/blog/show-all-posts").then((res) => {
  //     setPosts(res.data.blogs);
  //   });
  // }

  // useEffect(() => {
  //   fetchData();
  // }, []);
  return (
    <>
      {popularPosts &&
        popularPosts.map((post, index) => {
          const date = new Date(post?.createdAt);
          const options = {
            weekday: "long",
            year: "numeric",
            month: "long",
            day: "numeric",
            hour: "numeric",
            minute: "numeric",
          };
          const formattedDate = date.toLocaleDateString("en-US", options);
          function extractPlainTextFromHtml(html) {
            const parser = new DOMParser();
            const doc = parser.parseFromString(html, "text/html");
            return doc.body.textContent || "";
          }

          // Example usage:
          const postContent = post.content;
          const plainContent = extractPlainTextFromHtml(postContent);
          const shortContent = plainContent.substring(0, 45);

          const postTitle = post.title;
          const plainTitle = extractPlainTextFromHtml(postTitle);
          const shortTitle = plainTitle.substring(0, 20);
          return (
            <div className="card p-1 mt-3" key={index}>
              <Link
                className="p-0 m-0 text-decoration-none text-dark"
                to={`/blogPage/${post._id}`}
              >
                <div className="row">
                  <div className="col-md-8">
                    <div className="card-body m-0 p-1">
                      <h5 className="card-title">{shortTitle}</h5>
                      <p className="card-text">{shortContent}</p>
                      <p className="pt-3 mb-0">
                        <small className="text-muted pt-3">
                          {formattedDate}
                        </small>
                      </p>
                    </div>
                  </div>
                  <div className="col-md-4 p-0">
                    <img
                      src={`http://localhost:8080/${post.path}`}
                      className="rounded-start m-0 p-0"
                      width={92}
                      height={95}
                      alt="..."
                    />
                  </div>
                </div>
              </Link>
            </div>
          );
        })}
    </>
  );
};

export default PopularPost;
