import React from "react";
import "./App.css";
import { Routes, Route, Navigate } from "react-router-dom";
import HomePage from "./pages/HomePage";
import SearchPage from "./pages/SearchPage";
import BlogPage from "./pages/BlogPage";
import About from "./pages/About";
import Contact from "./pages/Contact";
import Login from "./pages/Auth/Login";
import LogOut from "./pages/Auth/LogOut";
import ForgotPassword from "./pages/Auth/ForgotPassword";
import Register from "./pages/Auth/Register";
import ScrollToTop from "./components/ScrollToTop";
import DashboardPage from "./pages/Author/DashboardPage";
import CreateNewPost from "./pages/Author/CreateNewPost";
import ShowAllPosts from "./pages/Author/ShowAllPosts";
import ShowAllComments from "./pages/Author/ShowAllComments";
import UserProfile from "./pages/Author/UserProfile";
import TestActive from "./TestActive";
import CategoryPage from "./pages/Author/CategoryPage";
import EditPostPage from "./pages/Author/EditPostPage";
import ByCategoryPage from "./pages/ByCategoryPage";
import UserContactUs from "./pages/Author/UserContactUs";
import Pagenotfound from "./pages/Pagenotfound";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const loginFeature = localStorage.getItem("loginFeature");
    setIsAuthenticated(!!loginFeature);
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem("loginFeature");
    setIsAuthenticated(false);
  };

  return (
    <>
      <ScrollToTop />
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<HomePage />} />
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/blogPage" element={<BlogPage />} />
        <Route path="/blogPage/:id" element={<BlogPage />} />
        <Route path="/search-page" element={<SearchPage />} />
        <Route path="/category-page" element={<ByCategoryPage />} />
        <Route path="/register" element={<Register />} />

        {/* Private Routes */}
        <PrivateRoute path="/dashboard" element={<DashboardPage />} />
        <PrivateRoute path="/create-new-post" element={<CreateNewPost />} />
        <PrivateRoute path="/show-all-posts" element={<ShowAllPosts />} />
        <PrivateRoute path="/show-all-comments" element={<ShowAllComments />} />
        <PrivateRoute path="/user-profile" element={<UserProfile />} />
        <PrivateRoute path="/show-category" element={<CategoryPage />} />
        <PrivateRoute path="/editBlog/:id" element={<EditPostPage />} />
        <PrivateRoute path="/user-contact-us" element={<UserContactUs />} />

        {/* Auth Routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/logout" element={<LogOut />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />

        {/* Test Route */}
        <Route path="/test" element={<TestActive />} />

        {/* 404 Route */}
        <Route path="*" element={<Pagenotfound />} />
      </Routes>
    </>
  );
}

export default App;

