import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./author.css";
import { useDispatch, useSelector } from "react-redux";
import { open, close } from "../../store/features/sidebarSlice";

function SideBar() {
  const [activeLink, setActiveLink] = useState(null);
  const toggle = useSelector((state) => state.sideBarReducer.value);
  const dispatch = useDispatch();

  const handleLinkClick = (index) => {
    setActiveLink(index);
  };
  return (
    <>
      <div
        className={`text-dark smSidebar50vh m-0 ${toggle}  `}
        style={{
          backgroundColor: "#f4f0ec",
          maxWidth: "300px",
          height: "100vh",
          zIndex: "100",
          position: "absolute",
          top: "11%",
        }}
      >
        <div className="d-flex justify-content-end p-1">
          <i
            onClick={() => {
              dispatch(close());
            }}
            className="fa-solid fa-xmark onHover fa-2x btn"
          ></i>
        </div>
        <div>
          <div className=" pt-1 mx-5">
            <NavLink
              to="/create-new-post "
              className="btn btn-secondary text-light fs-4 mx-5  rounded-2 "
            >
              <span className="pt-3  ">
                <i className="fa-sharp fa-solid fa-plus me-3"></i>New Post
              </span>
            </NavLink>
          </div>
        </div>
        <hr className="w-75 mx-auto" />
        <ul className=" m-0 px-0" style={{ listStyleType: "none" }}>
          <li
            className={
              activeLink === 0
                ? "py-3 sidebar-li active-li "
                : "py-3 sidebar-li "
            }
            onClick={() => handleLinkClick(0)}
          >
            <NavLink
              className="text-decoration-none  text-dark fs-4 px-5 "
              to="/show-all-posts"
              onClick={() => handleLinkClick(0)}
            >
              <i className="fa-solid fa-blog me-3"></i> Posts
            </NavLink>
          </li>
          <li
            className={
              activeLink === 1
                ? "py-3 sidebar-li active-li "
                : "py-3 sidebar-li "
            }
            onClick={() => handleLinkClick(1)}
          >
            <NavLink
              className="text-decoration-none  text-dark fs-4 px-5 "
              to="/show-all-comments"
              onClick={() => handleLinkClick(1)}
            >
              <i className="fa-solid fa-comments me-3"></i>Comments
            </NavLink>
          </li>
          <li
            className={
              activeLink === 2
                ? "py-3 sidebar-li active-li "
                : "py-3 sidebar-li "
            }
            onClick={() => handleLinkClick(2)}
          >
            <NavLink
              className="text-decoration-none  text-dark fs-4 px-5 "
              to="/user-profile"
              onClick={() => handleLinkClick(2)}
            >
              <i className="fa-solid fa-gear me-3"></i> Settings
            </NavLink>
          </li>
        </ul>
        <hr className="w-75 mx-auto" />
        <ul className=" m-0 px-0" style={{ listStyleType: "none" }}>
          <li className=" py-3 sidebar-li  " style={{}}>
            <NavLink
              className="text-decoration-none  text-dark fs-4 px-5 "
              to="/"
            >
              <i className="fa-solid fa-arrow-up-right-from-square me-3"></i>{" "}
              View Blogs
            </NavLink>
          </li>
        </ul>
      </div>
    </>
  );
}

export default SideBar;
