import React, { useEffect, useState } from "react";
import { NavLink, Link } from "react-router-dom";

const Header = () => {
  const [user, setUser] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  var token = JSON.parse(localStorage.getItem("token"));
  const handelLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setIsLoggedIn(false);
  };
  useEffect(() => {
    setUser(JSON.parse(localStorage.getItem("user")));
    if (token) {
      setIsLoggedIn(true);
    }
  }, [isLoggedIn, token]);
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <div className="d-flex align-items-center">
            <Link
              className="navbar-brand ms-5"
              to="/"
              style={{ height: "60px" }}
            >
              <span className="ms-3 fs-1 fw-bold align-baseline">
                <img
                  src="/images/logo-light.png "
                  className="img-fluid rounded m-2"
                  width={50}
                  alt="logo"
                />
                BlogBooth
              </span>
            </Link>
          </div>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarTogglerDemo02"
            aria-controls="navbarTogglerDemo02"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon" />
          </button>

          <div
            className="collapse navbar-collapse me-5 fs-3"
            id="navbarTogglerDemo02"
          >
            <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
              <li className="nav-item m-2 p-2">
                <NavLink className="nav-link" to="/">
                  Home
                </NavLink>
              </li>
              <li className="nav-item m-2 p-2">
                <NavLink className="nav-link" to="/blogPage">
                  Blog
                </NavLink>
              </li>
              {token ? (
                <>
                  <li className="nav-item m-2 p-2">
                    <NavLink className="nav-link" to="/dashboard">
                      {user && user.name}
                    </NavLink>
                  </li>
                  <li className="nav-item m-2 p-2">
                    <NavLink className="nav-link" onClick={handelLogout} to="#">
                      Logout
                    </NavLink>
                  </li>
                </>
              ) : (
                <>
                  <li className="nav-item m-2 p-2">
                    <NavLink className="nav-link" to="/login">
                      Login
                    </NavLink>
                  </li>
                  <li className="nav-item m-2 p-2">
                    <NavLink className="nav-link" to="/register">
                      Register
                    </NavLink>
                  </li>
                </>
              )}
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Header;
