import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import DisplayData from "./dangerouslySetInnerHTML";
import DashboardPage from "./DashboardPage";

const ShowAllPosts = () => {
  const [posts, setPosts] = useState([]);
  const [message, setMessage] = useState("");

  if (message) {
    setTimeout(() => {
      setMessage("");
    }, 3000);
  }

  async function fetchData() {
    await axios
      .get("http://localhost:8080/api/blog/show-all-posts")
      .then((res) => {
        setPosts(res.data.blogs);
      });
  }

  const handleDelete = async (id) => {
    try {
      await axios
        .delete(`http://localhost:8080/api/blog/delete-blog/${id}`)
        .then((res) => {
          setMessage(res.data.message);
        });
      fetchData();
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);
  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <h3 className="display-5 py-3 border-bottom">All Blogs</h3>
          </div>
          {message && (
            <div className="col-md-12">
              <div className="alert alert-success" role="alert">
                {message}
              </div>
            </div>
          )}

          {/* =========Start There===================================== */}
          {posts.length > 0 ? (
            posts.map((post, index) => {
              function extractPlainTextFromHtml(html) {
                const parser = new DOMParser();
                const doc = parser.parseFromString(html, "text/html");
                return doc.body.textContent || "";
              }

              // Example usage:
              const html = post.content;
              const plainText = extractPlainTextFromHtml(html);
              console.log(plainText); // Output: "This is bold text in a paragraph."

              // const description = <DisplayData data={post.content} />;
              // console.log(description);
              // const description1 = description.substring(0, 60);
              // const description1 = description.substring(0, 60);

              return (
                <div className="col-md-4 my-4" key={index}>
                  <div
                    className="card"
                    style={{ width: "280px", height: "auto" }}
                  >
                    <img
                      src={`http://localhost:8080/${post.path}`}
                      className="img-fluid rounded-start"
                      alt="..."
                    />
                    <div className="card-body mt-0 mb-0 pt-0">
                      {post.category && (
                        <p
                          className="mx-1 m-0 p-0 "
                          style={{ fontSize: "4px" }}
                        >
                          Category :{" "}
                          <span className="text-primary m-0 p-0 ">
                            {post.category.name}
                          </span>
                        </p>
                      )}
                      <h4 className="card-title mb-0  ">
                        {post.title.substring(0, 33)}...
                      </h4>

                      <div className="card-text  ">
                        {plainText}
                        {/* {post.content.substring(0, 60)}... */}
                        {post.content.length > 60 && "..."}
                      </div>
                      <div className="clearfix"></div>
                      <div className="d-flex justify-content-between align-items-center">
                        <Link
                          to={`/blogPage/${post._id}`}
                          className="btn btn-secondary"
                        >
                          Show More
                        </Link>
                        <div className="w-25 d-flex justify-content-between  ">
                          <Link to={`/editBlog/${post._id}`} className="">
                            <i className="fa-solid fa-pen-to-square fa-2x text-muted onHover-icons"></i>
                          </Link>
                          <button
                            onClick={() => {
                              handleDelete(post._id);
                            }}
                            // to={`/delete-blog/${post._id}`}
                            className="border border-white"
                          >
                            <i className="fa-solid fa-trash fa-2x text-muted onHover-icons"></i>
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="" style={{ height: "75vh", with: "100%" }}>
              <div className="d-flex justify-content-center ">
                <div className="spinner-border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            </div>
          )}

          {/* ======================Extra======================================= */}
        </div>
      </div>
    </DashboardPage>
  );
};

export default ShowAllPosts;
