import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import DashboardPage from "./DashboardPage";

const ShowAllPosts = () => {
  const [posts, setPosts] = useState([]);
  const [message, setMessage] = useState("");
  const [blogCount, setBlogCount] = useState("");
  const [page, setPage] = useState(1);
  const [dataLength, setDataLength] = useState("");
  const [totalPages, setTotalPages] = useState(0);
  // eslint-disable-next-line
  const [categoryId, setCategoryId] = useState(0);
  if (message) {
    setTimeout(() => {
      setMessage("");
    }, 3000);
  }

  async function fetchData() {
    await axios.get("http://localhost:8080/api/blog/show-posts").then((res) => {
      setPosts(res.data.blogs);
      setBlogCount(res.data.BlogCount);
    });
  }

  const handleDelete = async (id) => {
    try {
      await axios
        .delete(`http://localhost:8080/api/blog/delete-blog/${id}`)
        .then((res) => {
          setMessage(res.data.message);
        });
      fetchData();
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    async function fetchData() {
      await axios
        .get(`http://localhost:8080/api/blog/show-posts?page=${page}`)
        .then((res) => {
          setPosts(res.data.blogs);
          setDataLength(res.data.BlogCount);
          setTotalPages(Math.ceil(res.data.BlogCount / 7));
        });
    }
    fetchData();
  }, [page]);

  function handlePaginationClick(newPage) {
    window.scrollTo(0, 0);
    setPage(newPage);
  }

  const paginationButtons = [];
  for (let i = 1; i <= totalPages; i++) {
    paginationButtons.push(
      <button
        key={i}
        className={`btn btn-secondary mx-3 px-4 ${i === page ? "active" : ""}`}
        onClick={() => {
          handlePaginationClick(i);
        }}
      >
        {i}
      </button>
    );
  }
  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <h3 className="display-5 py-3 border-bottom">
              All Blogs{" "}
              <small className="fs-4 ">({blogCount ? blogCount : "0"})</small>
            </h3>
          </div>
          {message && (
            <div className="col-md-12">
              <div className="alert alert-success" role="alert">
                {message}
              </div>
            </div>
          )}

          {/* =========Start There===================================== */}
          {posts.length > 0 ? (
            posts.map((post, index) => {
              function extractPlainTextFromHtml(html) {
                const parser = new DOMParser();
                const doc = parser.parseFromString(html, "text/html");
                return doc.body.textContent || "";
              }

              // Example usage:
              const postContent = post.content;
              const plainContent = extractPlainTextFromHtml(postContent);
              const shortContent = plainContent.substring(0, 60);

              const postTitle = post.title;
              const plainTitle = extractPlainTextFromHtml(postTitle);
              const shortTitle = plainTitle.substring(0, 25);
              return (
                <div className="col-md-4 my-4" key={index}>
                  <div
                    className="card"
                    style={{ width: "280px", height: "auto" }}
                  >
                    <img
                      src={`http://localhost:8080/${post.path}`}
                      className="img-fluid rounded-start"
                      alt="..."
                    />
                    <div className="card-body mt-0 mb-0 pt-0">
                      {post.category && (
                        <p
                          className="mx-1 m-0 p-0 "
                          style={{ fontSize: "4px" }}
                        >
                          Category :{" "}
                          <span className="text-primary m-0 p-0 ">
                            {post.category.name}
                          </span>
                        </p>
                      )}
                      <h4 className="card-title mb-0  ">{shortTitle}</h4>

                      <div className="card-text mb-2 text-indent-css ">
                        {shortContent}
                        {/* {post.content.substring(0, 60)}... */}
                        {/* {post.content.length > 60 && "..."} */}
                      </div>
                      <div className="clearfix"></div>
                      <div className="d-flex justify-content-between align-items-center">
                        <Link
                          to={`/blogPage/${post._id}`}
                          className="btn btn-secondary"
                        >
                          Show More
                        </Link>
                        <div className="w-25 d-flex justify-content-between  ">
                          <Link to={`/editBlog/${post._id}`} className="">
                            <i className="fa-solid fa-pen-to-square fa-2x text-muted onHover-icons"></i>
                          </Link>
                          <button
                            onClick={() => {
                              handleDelete(post._id);
                            }}
                            // to={`/delete-blog/${post._id}`}
                            className="border border-white"
                          >
                            <i className="fa-solid fa-trash fa-2x text-muted onHover-icons"></i>
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="" style={{ height: "75vh", with: "100%" }}>
              <div className="d-flex justify-content-center ">
                <div className="spinner-border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            </div>
          )}

          <div className="d-flex justify-content-center mt-4">
            {paginationButtons}
          </div>

          {/* ======================Extra======================================= */}
        </div>
      </div>
    </DashboardPage>
  );
};

export default ShowAllPosts;
