import axios from "axios";
import React, { useState } from "react";

const Comments = () => {
  const [name, setName] = useState("");
  const [comment, setComment] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      name: name,
      comment: comment,
    };
    axios
      .post("http://localhost:8080/api/blog/add-comment", data)
      .then((res) => {
        console.log(res.data);
      });
    console.log(name, comment);
    setName("");
    setComment("");
  };
  return (
    <>
      <div className="container">
        <div className="row my-5">
          <div className="card mx-auto bg-light">
            <h3 className="my-2 py-2 fs-4">Leave a Comment</h3>
            <div className="col-md-12  float-start ">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="Name ..."
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="col-md-12 my-2 py-2">
              <textarea
                className="form-control form-control-lg"
                placeholder="Your Comments Here ..."
                value={comment}
                onChange={(e) => setComment(e.target.value)}
              />
            </div>{" "}
            <div className="col-md-12  my-1 py-1">
              <button
                onClick={handleSubmit}
                className="form-control form-control-lg btn btn-secondary btn-lg"
              >
                Submit
              </button>
            </div>
          </div>
        </div>
      </div>{" "}
    </>
  );
};

export default Comments;
