import React, { useEffect, useState } from "react";
import DashboardPage from "./DashboardPage";
import { Link } from "react-router-dom";
import axios from "axios";

const ShowAllComments = () => {
  const [comments, setComments] = useState([]);

  const token = localStorage.getItem("token").slice(1, -1);
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  async function fetchData() {
    await axios
      .get("/api/blog/show-all-posts", config)
      .then((res) => {
        const allComments = res.data.blogs.map((blogs) => blogs.comment);
        setComments(allComments);
        const uniqueComments = allComments.map(
          (comments, i) => comments[i].comment
        );
        //   .flat();

        // =================
        // const commentArray = allComments.map((innerArray) => {
        //   return innerArray.map((object) => {
        //     return object.comment;
        //   });
        // });
        // // =====================

        console.log(allComments, "allComments");
        // console.log(res.data.blogs, "res.data.blogs");
        console.log(uniqueComments, "uniqueComments");
      })
      .catch((error) => {
        console.log(error);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <DashboardPage>
      <div className="display-5 border-bottom py-3">All Comments</div>

      {/* --------------Map Here---------------- */}

      {comments &&
        comments.map((comment) => {
          console.log(comment, "comment");
          comment.map((object) => {
            console.log(object, "object");
            return (
              <>
                <div className="card my-3">
                  <div className="card-header">
                    <div className="d-flex justify-content-between align-items-center">
                      <Link to="/blogPage" className="btn btn-secondary">
                        {object.username}
                      </Link>
                      <div className="">
                        <Link to="/blogPage" className="">
                          <i className="fa-solid fa-pen-to-square fa-2x text-muted onHover-icons"></i>
                        </Link>
                        <div className="mx-3 d-inline"></div>
                        <Link to="/blogPage" className="">
                          <i className="fa-solid fa-trash fa-2x text-muted onHover-icons"></i>
                        </Link>
                      </div>
                    </div>
                  </div>
                  <div className="card-body">
                    <blockquote className="blockquote mb-0">
                      <p>{object.comment}</p>
                      <footer className="blockquote-footer">
                        Time of Comments
                        <span className="fw-bold"> Comments On which Post</span>
                      </footer>
                    </blockquote>
                  </div>
                </div>
              </>
            );
          });
        })}
    </DashboardPage>
  );
};

export default ShowAllComments;
