import { Link, useParams } from "react-router-dom";
import Comments from "../components/Comments";
import Layout from "../components/layout/Layout";
import axios from "axios";
import React, { useEffect, useState } from "react";
import DisplayData from "./Author/dangerouslySetInnerHTML";

const BlogPage = () => {
  var { id } = useParams();
  const [blog, setBlog] = useState("");
  async function fetchData() {
    //  await id = useParams();

    await axios
      .get(`http://localhost:8080/api/blog/get-blog/${id}`)
      .then((res) => {
        console.log(res.data.blog);
        setBlog(res.data.blog);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-9 mx-auto bg-light">
            <h1 className="mt-5">
              <DisplayData data={blog.title} />
            </h1>
            <p className="mb-5">
              {blog.createdAt} by{" "}
              <Link to="/" style={{ color: "gray" }}>
                Mark
              </Link>
            </p>
            <div
              id="content-main"
              className="left relative"
              style={{ height: "auto !important" }}
            >
              <div>
                <img
                  src={`http://localhost:8080/${blog.path}`}
                  className="img-fluid"
                  alt="Blog Banner"
                />
              </div>

              <div>
                <p>
                  <DisplayData data={blog.content} />
                </p>
              </div>
            </div>

            {/* =============================================================== */}
            <div className="display-1">Comments</div>

            <div className="card">
              <div className="card-header">Name</div>
              <div className="card-body">
                <blockquote className="blockquote mb-0">
                  <p>Comments</p>
                  <footer className="blockquote-footer">
                    Someone famous in{"{"}" "{"}"}
                    <cite title="Source Title">Source Title</cite>
                  </footer>
                </blockquote>
              </div>
            </div>

            <Comments blogId={blog._id} />
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
