import React, { useEffect, useState } from "react";
import Card from "../components/Card";
import FeatureCard from "../components/FeatureCard";
import PopularPost from "../components/PopularPost";
import CategoryList from "../components/CategoryList";
import Layout from "../components/layout/Layout";
import axios from "axios";
import { Link } from "react-router-dom";
// import InfiniteScroll from "react-infinite-scroll-component";
const HomePage = () => {
  const [posts, setPosts] = useState([]);
  const [popularPosts, setPopularPost] = useState([]);
  const [featurePost, setFeaturePost] = useState("");
  const [page, setPage] = useState(1);
  const [dataLength, setDataLength] = useState("");
  const [totalPages, setTotalPages] = useState(0);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    async function fetchCategory() {
      await axios
        .get("http://localhost:8080/api/category/get-all")
        .then((res) => {
          setCategories(res.data.category);
        });
    }
    fetchCategory();
  }, []);

  useEffect(() => {
    async function fetchData() {
      await axios.get("/api/blog/show-all-posts").then((res) => {
        setPosts(res.data.blogs);
        setPopularPost(res.data.popularPosts);
      });
    }
    fetchData();
  }, []);

  useEffect(() => {
    async function fetchFeaturePost() {
      const { data } = await axios.get(
        "http://localhost:8080/api/blog/show-feature-post"
      );
      setFeaturePost(data.blogs[0]);
    }
    fetchFeaturePost();
  }, []);

  useEffect(() => {
    async function fetchData() {
      await axios
        .get(`http://localhost:8080/api/blog/show-posts?page=${page}`)
        .then((res) => {
          setPosts(res.data.blogs);
          setDataLength(res.data.BlogCount);
          setTotalPages(Math.ceil(res.data.BlogCount / 7));
        });
    }
    fetchData();
  }, [page]);

  function handlePaginationClick(newPage) {
    window.scrollTo(0, 350);
    setPage(newPage);
  }

  const paginationButtons = [];
  for (let i = 1; i <= totalPages; i++) {
    paginationButtons.push(
      <button
        key={i}
        className={`btn btn-secondary mx-3 px-4 ${i === page ? "active" : ""}`}
        onClick={() => {
          handlePaginationClick(i);
        }}
      >
        {i}
      </button>
    );
  }

  return (
    <Layout>
      <FeatureCard featurePost={featurePost} />
      <div className="row">
        {/* Left Panel of Page */}

        <div className="col-md-8 m-3 p-3">
          <h4 className="display-5 mx-2 p-2">Recent Posts</h4>
          <Card posts={posts} />
        </div>

        {/* Right Panel of Page */}
        <div className="col-md-3 m-3 p-3">
          <h4 className="display-5 mx-2 p-2">Popular Posts</h4>
          {/* =====================Small Card=========== */}
          <PopularPost popularPosts={popularPosts} />
          <CategoryList categories={categories} />
        </div>
        <div className="row mt-0">
          <div className="col-md-8">
            {/* <div className="mx-auto w-25">
              <Link
                to="/category-page"
                className="btn btn-secondary btn-lg m-3 p-2"
              >
                View Category Page
              </Link>
            </div> */}

            <div className="d-flex justify-content-center my-4">
              {paginationButtons}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default HomePage;
