import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import DashboardPage from "./DashboardPage";

const UserProfile = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [answer, setAnswer] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token").slice(1, -1);
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "name":
        setName(value);
        break;
      case "email":
        setEmail(value);
        break;
      case "password":
        setPassword(value);
        break;
      case "phone":
        setPhone(value);
        break;
      case "address":
        setAddress(value);
        break;
      case "answer":
        setAnswer(value);
        break;
      default:
        break;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/update-profile",
        {
          name,
          email,
          password,
          phone,
          address,
          answer,
        },
        config
      );
      if (res.data.success === true) {
        toast.success("Updated Successfully");
        navigate("/dashboard");
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  useEffect(() => {
    axios.get("http://localhost:8080/api/auth/profile", config).then((res) => {
      if (res.data.success === true) {
        setName(res.data.user.name);
        setEmail(res.data.user.email);
        setPhone(res.data.user.phone);
        setAddress(res.data.user.address);
        setAnswer(res.data.user.answer);
      } else {
        toast.error(res.data.message);
      }
    });
  }, [config]);
  return (
    <DashboardPage>
      <div className="container">
        <div className="row">
          <div className="display-5 border-bottom py-3">Update Profile </div>

          <div className="col-md-12 my-1 ">
            <form className="" onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="fs-4 p-1">Name</label>
                <input
                  value={name}
                  onChange={handleChange}
                  type="text"
                  className="form-control form-control-lg"
                  name="name"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-4 p-1">Email</label>
                <input
                  value={email}
                  onChange={handleChange}
                  type="email"
                  className="form-control form-control-lg"
                  name="email"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-4 p-1">Password</label>
                <input
                  value={password}
                  onChange={handleChange}
                  type="password"
                  className="form-control form-control-lg"
                  name="password"
                  placeholder="Password Remember"
                  // disabled
                />
              </div>
              <div className="form-group">
                <label className="fs-4 p-1">Phone</label>
                <input
                  value={phone}
                  onChange={handleChange}
                  type="number"
                  className="form-control form-control-lg"
                  name="phone"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-4 p-1">Address</label>
                <input
                  value={address}
                  onChange={handleChange}
                  type="text"
                  className="form-control form-control-lg"
                  name="address"
                  required
                />
              </div>{" "}
              <div className="form-group">
                <label className="fs-4 p-1">What is your favourite Book</label>
                <input
                  value={answer}
                  onChange={handleChange}
                  type="text"
                  className="form-control form-control-lg"
                  name="answer"
                  required
                />
              </div>
              <div className="form-group mt-3">
                <button
                  type="submit"
                  className="form-control form-control-lg btn btn-secondary btn-lg btn-block p-2 my-3"
                >
                  Update Profile
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </DashboardPage>
  );
};

export default UserProfile;
