import React, { useState } from "react";
import DashboardPage from "./DashboardPage.js";
import axios from "axios";

const CategoryPage = () => {
  const [name, setName] = useState("");

  const handleClick = (e) => {
    e.preventDefault();
    console.log(name);
    axios.post("/api/category/create", {
      name: name,
    });
  };

  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row mt-5">
          <div className="w-100">
            {/* =================Add Category section ================= */}
            {/* <hr className="card-title ms-0 ps-0 "></hr> */}

            <h3 className="card-title ms-0 ps-0  ">Create New Category</h3>

            <div className=" mb-0">
              <div className="card-body mb-0">
                <div className="d-flex">
                  <p className="card-text w-75 pt-2 mb-1 pb-0">
                    <input
                      type="text"
                      className="form-control fs-4"
                      placeholder="Category Name ..."
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      required
                    />
                  </p>

                  <div className="card-text pt-2 ">
                    <button
                      onClick={handleClick}
                      className=" form-control btn btn-secondary fs-4"
                    >
                      Add New Category
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* =================Add Category end section ================= */}

        {/* =================Add Table section ================= */}

        <div className="row mt-5">
          {/* <hr className="card-title "></hr> */}

          <h3 className="card-title ms-0 ps-0 ">Category List</h3>

          <table className="table table-striped table-hover mt-3">
            <thead>
              <tr>
                <th scope="col">No.</th>
                <th scope="col">Category Name</th>
                <th scope="col">Edit</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th>id</th>
                <td>name</td>
                <td>edit button</td>
              </tr>
              <tr>
                <th>id</th>
                <td>name</td>
                <td>edit button</td>
              </tr>
              <tr>
                <th>id</th>
                <td>name</td>
                <td>edit button</td>
              </tr>
              <tr>
                <th>id</th>
                <td>name</td>
                <td>edit button</td>
              </tr>
            </tbody>
          </table>
        </div>
        {/* =================Add Table section ================= */}
      </div>
    </DashboardPage>
  );
};

export default CategoryPage;
