import React, { useEffect, useState } from "react";
import DashboardPage from "./DashboardPage.js";
import axios from "axios";
// import { toast } from "react-toastify";

const CategoryPage = () => {
  const [name, setName] = useState("");
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [delMessage, setDelMessage] = useState("");
  const [editMessage, setEditMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [loadDel, setLoadDel] = useState(false);
  const [loadEdit, setLoadEdit] = useState(false);
  const [categories, setCategories] = useState([]);

  if (error || message || delMessage || editMessage) {
    setTimeout(() => {
      setError("");
      setMessage("");
      setDelMessage("");
      setEditMessage("");
    }, 3000);
  }

  const handleClick = async (e) => {
    e.preventDefault();
    console.log(name);
    try {
      setLoading(true);
      const res = await axios.post(
        "http://localhost:8080/api/category/create",
        {
          name: name,
        }
      );
      if (res.data.success === true) {
        // toast.success("Category Created Successfully");
        setLoading(false);
        setMessage(res.data.message);
        setName("");
      } else {
        setError(res.data.message);
        console.log(res.data.message);
        setLoading(false);
      }
    } catch (error) {
      setError(error.message);
      console.log(error.message);
      setLoading(false);
    }
  };

  const handleDelete = (id) => {
    try {
      setLoadDel(true);
      const res = axios.delete(
        `http://localhost:8080/api/category/delete/${id}`
      );
      if (res.data.success === true) {
        // toast.success("Category Deleted Successfully");
        setLoadDel(false);
        setDelMessage(res.data.message);
      } else {
        setDelMessage(res.data.message);
        console.log(res.data.message);
        setLoadDel(false);
      }
    } catch (error) {
      setDelMessage(error.message);
      setLoadDel(false);
    }
  };

  const handleEdit = (e, id) => {
    e.preventDefault();
    try {
      setLoading(true);
      const res = axios.delete(
        `http://localhost:8080/api/category/delete/${id}`
      );
      if (res.data.success === true) {
        // toast.success("Category Deleted Successfully");
        setLoading(false);
        setMessage(res.data.message);
        console.log(res.data.message);
      } else {
        setError(res.data.message);
        console.log(res.data.message);
        setLoading(false);
      }
    } catch (error) {
      setError(error.message);
    }
  };

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await axios.get(
          "http://localhost:8080/api/category/get-all"
        );
        setCategories(res.data.category);
      } catch (error) {
        console.log(error.message);
      }
    };
    fetchCategories();
  }, [loading, delMessage, setName]);

  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row mt-5">
          <div className="w-100">
            {/* =================Add Category section ================= */}
            {/* <hr className="card-title ms-0 ps-0 "></hr> */}
            <h3 className="card-title ms-0 ps-0  ">Create New Category</h3>

            <div className=" mb-0">
              <div className="card-body mb-0">
                {error && <p className="text-danger m-1">{error}</p>}
                {message && <p className="text-success m-1">{message}</p>}
                <div className="d-flex">
                  <p className="card-text w-75 pt-2 mb-1 pb-0">
                    <input
                      type="text"
                      className="form-control fs-4"
                      placeholder="Category Name ..."
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      required
                    />
                  </p>

                  <div className="card-text pt-2 ">
                    <button
                      onClick={handleClick}
                      className=" form-control btn btn-secondary fs-4"
                    >
                      {loading ? (
                        <div className="spinner-border" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </div>
                      ) : (
                        "Add New Category"
                      )}
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* =================Add Category end section ================= */}

        {/* =================Add Table section ================= */}

        <div className="row mt-5">
          {/* <hr className="card-title "></hr> */}

          <h3 className="card-title ms-0 ps-0 ">Category List</h3>
          <div
            className="bg-light"
            style={{ height: "400px", overflow: "scroll" }}
          >
            <div className="text-center w-50 mx-auto">
              {delMessage && <p className="text-success m-1">{delMessage}</p>}
            </div>
            <table className="table table-striped table-hover mt-3">
              <thead>
                <tr>
                  <th scope="col">No.</th>
                  <th scope="col">Category Name</th>
                  <th scope="col" className="text-center">
                    Edit
                  </th>
                  <th scope="col" className="text-center">
                    Delete
                  </th>
                </tr>
              </thead>
              <tbody>
                {categories &&
                  categories.map((category, index) => (
                    <tr key={category._id}>
                      <th scope="row">{index + 1}</th>
                      <th>{category.name}</th>

                      <th className="text-center">
                        <button
                          className="btn btn-info btn-sm"
                          onClick={() => {
                            handleEdit(category._id);
                          }}
                        >
                          Edit
                        </button>
                      </th>
                      <th className="text-center">
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => {
                            handleDelete(category._id);
                          }}
                        >
                          {loadDel ? (
                            <div className="spinner-border" role="status">
                              <span className="visually-hidden">
                                Loading...
                              </span>
                            </div>
                          ) : (
                            "Delete"
                          )}
                        </button>
                      </th>
                    </tr>
                  ))}
                <tr>
                  <th>id</th>
                  <td>name</td>
                  <td>edit button</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
        {/* =================Add Table section ================= */}
      </div>
    </DashboardPage>
  );
};

export default CategoryPage;
