import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { useNavigate, useLocation, Link } from "react-router-dom";
import PopularPost from "../../components/PopularPost";
import { useDispatch } from "react-redux";
import { login } from "../../store/features/LoginFeature/loginSlice.js";
import Layout from "../../components/layout/Layout";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const location = useLocation();
  const [error, setError] = useState("");

  // const loginFeature = useSelector((state) => state.loginFeature.value);
  const dispatch = useDispatch();

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "email":
        setEmail(value);
        break;
      case "password":
        setPassword(value);
        break;
      default:
        break;
    }
  };

  useEffect(() => {
    if (error) {
      const timeoutId = setTimeout(() => {
        setError("");
      }, 3000);

      return () => clearTimeout(timeoutId);
    }
  }, [error]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/api/auth/login", {
        email,
        password,
      });
      if (res.data.success === true) {
        localStorage.setItem("user", JSON.stringify(res.data.user));
        localStorage.setItem("token", JSON.stringify(res.data.token));
        toast.success("Login Successfully");
        dispatch(login());
        if (res.data.message) {
          navigate(location.state || "/");
        }
      } else {
        setError(res.data.message);
        toast.error(res.data.message);
      }
    } catch (error) {
      setError(error.message);
      toast.error(error.message);
    }
  };
  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-6 card offset-md-1 my-auto px-5 bg-light">
            <h1 className="text-center display-4 mt-5">Log In User </h1>
            <form onSubmit={handleSubmit}>
              {error && (
                <div className="form-group form-control my-3 mx-auto text-danger">
                  {error}
                </div>
              )}
              <div className="form-group">
                <label className="fs-3 p-1">Email</label>
                <input
                  value={email}
                  onChange={handleChange}
                  type="email"
                  className="form-control form-control-lg"
                  name="email"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-3 p-1">Password</label>
                <input
                  value={password}
                  onChange={handleChange}
                  type="password"
                  className="form-control form-control-lg"
                  name="password"
                  required
                />
              </div>

              <div className="form-group mt-4 mx-auto">
                <Link to="/forget-password" className=" p-2 mt-0">
                  Forgot Password
                </Link>
              </div>

              <div className="form-group my-5">
                <button
                  type="submit"
                  className="form-control form-control-lg btn btn-secondary btn-lg btn-block p-2 mt-0"
                >
                  Login
                </button>
              </div>
            </form>
          </div>
          <div className="col-md-4 my-5 mx-auto">
            <h4 className="display-5 mx-2 p-2">Popular Posts</h4>

            <PopularPost />
          </div>
          <hr className="w-50 mx-auto my-5 text-dark" />
        </div>
      </div>
    </Layout>
  );
};

export default Login;
