import React, { useEffect, useState } from "react";
import DashboardPage from "./DashboardPage";
import { Link } from "react-router-dom";
import axios from "axios";
import Avatar from "react-avatar";

const ShowAllComments = () => {
  const [comments, setComments] = useState("");
  const [page, setPage] = useState(1);
  const [dataLength, setDataLength] = useState("");
  const [totalPages, setTotalPages] = useState(0);
  const token = localStorage.getItem("token").slice(1, -1);
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  // async function fetchData() {
  //   await axios
  //     .get("http://localhost:8080/api/blog//show-all-comments", config)
  //     .then((res) => {
  //       // const comments = res.data.blogs.map((blogs) => blogs.comment);
  //       setComments(res.data.comment);

  //       // console.log(allComments, "allComments");
  //       console.log(res.data.comment, "res.data.blogs");
  //       // console.log(uniqueComments, "uniqueComments");
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // }
  // useEffect(() => {
  //   fetchData();
  // }, []);

  useEffect(() => {
    async function fetchData() {
      await axios
        .get(
          `http://localhost:8080/api/blog/show-all-comments?page=${page}`,
          config
        )
        .then((res) => {
          setComments(res.data.comment);
          setDataLength(res.data.commentCount);
          setTotalPages(Math.ceil(res.data.commentCount / 7));
        });
    }
    fetchData();
  }, [page]);

  function handlePaginationClick(newPage) {
    window.scrollTo(0, 0);
    setPage(newPage);
  }

  const paginationButtons = [];

  for (let i = 0; i <= totalPages + 1; i++) {
    if (i === 0) {
      paginationButtons.push(
        <button
          key={"first"}
          className={`btn btn-secondary mx-3 px-4 ${
            page === 1 ? "disabled" : ""
          }`}
          onClick={() => {
            handlePaginationClick(1);
          }}
          disabled={page === 1}
        >
          <i className="fa-solid fa-less-than"></i>
        </button>
      );
    } else if (i === totalPages + 1) {
      paginationButtons.push(
        <button
          key={"last"}
          className={`btn btn-secondary mx-3 px-4 ${
            page === totalPages ? "disabled" : ""
          }`}
          onClick={() => {
            handlePaginationClick(totalPages);
          }}
          disabled={page === totalPages}
        >
          <i className="fa-solid fa-greater-than"></i>
        </button>
      );
    } else {
      paginationButtons.push(
        <button
          key={i}
          className={`btn btn-secondary mx-3 px-4 ${
            i === page ? "active" : ""
          }`}
          onClick={() => {
            handlePaginationClick(i);
          }}
        >
          {i}
        </button>
      );
    }
  }

  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <h3 className="display-5 py-3 border-bottom">
              All Blogs{" "}
              <small className="fs-4 ">({dataLength ? dataLength : "0"})</small>
              <small className="fs-6 float-end m-4 p-1">Page No. {page}</small>
            </h3>
          </div>
          {message && (
            <div className="col-md-12">
              <div className="alert alert-success" role="alert">
                {message}
              </div>
            </div>
          )}

          {/* =========Start There===================================== */}
          {posts.length > 0 ? (
            posts.map((post, index) => {
              function extractPlainTextFromHtml(html) {
                const parser = new DOMParser();
                const doc = parser.parseFromString(html, "text/html");
                return doc.body.textContent || "";
              }

              // Example usage:
              const postContent = post.content;
              const plainContent = extractPlainTextFromHtml(postContent);
              const shortContent = plainContent.substring(0, 60);

              const postTitle = post.title;
              const plainTitle = extractPlainTextFromHtml(postTitle);
              const shortTitle = plainTitle.substring(0, 25);
              return (
                <div className="col-md-4 my-4" key={index}>
                  <div
                    className="card"
                    style={{ width: "280px", height: "auto" }}
                  >
                    <img
                      src={`http://localhost:8080/${post.path}`}
                      className="img-fluid rounded-start"
                      alt="..."
                    />
                    <div className="card-body mt-0 mb-0 pt-0">
                      {post.category && (
                        <p
                          className="mx-1 m-0 p-0 "
                          style={{ fontSize: "4px" }}
                        >
                          Category :{" "}
                          <span className="text-primary m-0 p-0 ">
                            {post.category.name}
                          </span>
                        </p>
                      )}
                      <h4 className="card-title mb-0  ">{shortTitle}</h4>

                      <div className="card-text mb-2 text-indent-css ">
                        {shortContent}
                        {/* {post.content.substring(0, 60)}... */}
                        {/* {post.content.length > 60 && "..."} */}
                      </div>
                      <div className="clearfix"></div>
                      <div className="d-flex justify-content-between align-items-center">
                        <Link
                          to={`/blogPage/${post._id}`}
                          className="btn btn-secondary"
                        >
                          Show More
                        </Link>
                        <div className="w-25 d-flex justify-content-between  ">
                          <Link to={`/editBlog/${post._id}`} className="">
                            <i className="fa-solid fa-pen-to-square fa-2x text-muted onHover-icons"></i>
                          </Link>
                          <button
                            onClick={() => {
                              handleDelete(post._id);
                            }}
                            // to={`/delete-blog/${post._id}`}
                            className="border border-white"
                          >
                            <i className="fa-solid fa-trash fa-2x text-muted onHover-icons"></i>
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="" style={{ height: "75vh", with: "100%" }}>
              <div className="d-flex justify-content-center ">
                <div className="spinner-border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            </div>
          )}

          <div className="d-flex justify-content-center mt-4">
            {paginationButtons}
          </div>

          {/* ======================Extra======================================= */}
        </div>
      </div>
    </DashboardPage>
  );
};

export default ShowAllComments;
