import React, { useEffect, useState } from "react";
import axios from "axios";

const LikeBlog = (props) => {
  const { blogId } = props;
  const [likes, setLikes] = useState(0);
  const handleLike = () => {
    axios
      .put(`http://localhost:8080/api/blog/get-blog/${blogId}/like`)
      .then((res) => {
        setLikes(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/blog/get-blog/${blogId}/like`)
      .then((res) => {
        setLikes(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <>
      <div className="d-flex mx-auto justify-content-evenly w-50">
        <h5 className="fs-3 m-3">Like a Post :</h5>
        <button className="btn btn-success m-2 " onClick={handleLike}>
          Like
        </button>
        <p className="fs-3 m-3">{like.like}</p>
      </div>
      <hr className="w-50 mx-auto" />
    </>
  );
};

export default LikeBlog;
