import { Link, useParams } from "react-router-dom";
import Comments from "../components/Comments";
import Layout from "../components/layout/Layout";
import axios from "axios";
import React, { useEffect, useState } from "react";
import DisplayData from "./Author/dangerouslySetInnerHTML";

const BlogPage = () => {
  var { id } = useParams();
  const [blog, setBlog] = useState("");
  async function fetchData() {
    //  await id = useParams();
    await axios
      .get(`http://localhost:8080/api/blog/get-blog/${id}`)
      .then((res) => {
        console.log(res.data.blog);
        setBlog(res.data.blog);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);

  // ---------Date---------------
  const date = new Date(comment.createdAt);
  const options = {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
  };
  const formattedDate = date.toLocaleDateString("en-US", options);

  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-9 mx-auto bg-light">
            <h1 className="mt-5">
              <DisplayData data={blog.title} />
            </h1>
            <p className="mb-5">
              {blog.createdAt} by{" "}
              <Link to="/" style={{ color: "gray" }}>
                Mark
              </Link>
            </p>
            <div
              id="content-main"
              className="left relative"
              style={{ height: "auto !important" }}
            >
              <div>
                <img
                  src={`http://localhost:8080/${blog.path}`}
                  className="img-fluid"
                  alt="Blog Banner"
                />
              </div>

              <div>
                <p>
                  <DisplayData data={blog.content} />
                </p>
              </div>
            </div>

            {/* =============================================================== */}
            <div className="fs-2 my-3">
              Comments ( {blog && blog.comment.length} )
            </div>

            {blog.comment &&
              blog.comment.map((comment, index) => (
                <div className="card my-3" key={index}>
                  <div className="card-header">{comment.username}</div>
                  <div className="card-body">
                    <blockquote className="blockquote mb-0">
                      <p>{comment.comment}</p>
                      <footer className="blockquote-footer">
                        {comment.createdAt}
                      </footer>
                    </blockquote>
                  </div>
                </div>
              ))}

            <Comments blogId={blog._id} />
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
