import React from "react";
import { Link } from "react-router-dom";

const CategoryList = () => {
  const [category, setCategory] = useState([]);

  useEffect(() => {
    async function fetchData() {
      await axios
        .get("http://localhost:8080/api/category/get-all")
        .then((res) => {
          setPosts(res.data.blogs);
          setPopularPost(res.data.popularPosts);
        });
    }
    fetchData();
  }, []);

  return (
    <>
      <div className="card  card-body p-3 mt-5 ">
        <h4 className="display-4 font-italic card-title ">Post Category </h4>

        <ul className="list-unstyled m-2 card-text">
          <li>
            <Link
              className="nav-link text-dark m-2 "
              to="/"
              style={{ textDecoration: "none" }}
            >
              How-To
            </Link>
          </li>
          <li>
            <Link
              className="nav-link text-dark m-2"
              to="/"
              style={{ textDecoration: "none" }}
            >
              Technology
            </Link>
          </li>
          <li>
            <Link
              className="nav-link text-dark m-2"
              to="/"
              style={{ textDecoration: "none" }}
            >
              Mobile
            </Link>
          </li>
          <li>
            <Link
              className="nav-link text-dark m-2"
              to="/"
              style={{ textDecoration: "none" }}
            >
              Business
            </Link>
          </li>
          <li>
            <Link
              className="nav-link text-dark m-2"
              to="/"
              style={{ textDecoration: "none" }}
            >
              Gadgets
            </Link>
          </li>
          <li>
            <Link
              className="nav-link text-dark m-2"
              to="/"
              style={{ textDecoration: "none" }}
            >
              Startup
            </Link>
          </li>
          <li>
            <Link
              to="/"
              className="nav-link text-dark m-2"
              style={{ textDecoration: "none" }}
            >
              Telecom
            </Link>
          </li>
        </ul>
      </div>
    </>
  );
};

export default CategoryList;
