import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import DashboardPage from "./DashboardPage";

const ShowAllPosts = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get("/api/blog/show-all-posts").then((res) => {
      console.log(res.data.blogs);
      setPosts(res.data);
    });
  }, []);
  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <h3 className="display-5 py-3 border-bottom">All Blogs</h3>
          </div>

          {/* =========Start There===================================== */}

          <div className="col-md-4 my-4">
            <div className="card" style={{ width: "300px" }}>
              <img
                src="/images/posts-image/2p.webp"
                className="img-fluid rounded-start"
                alt="..."
              />
              <div className="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </p>
                <div className="d-flex justify-content-between align-items-center">
                  <Link to="/blogPage" className="btn btn-secondary">
                    Show More
                  </Link>
                  <div className="w-25 d-flex justify-content-between  ">
                    <Link to="/blogPage" className="">
                      <i className="fa-solid fa-pen-to-square fa-2x text-muted onHover-icons"></i>
                    </Link>
                    <Link to="/blogPage" className="">
                      <i className="fa-solid fa-trash fa-2x text-muted onHover-icons"></i>
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* ======================Extra======================================= */}
        </div>
      </div>
    </DashboardPage>
  );
};

export default ShowAllPosts;
