import React, { useEffect, useState } from "react";
import DashboardPage from "./DashboardPage";
import { Link } from "react-router-dom";
import axios from "axios";
import Avatar from "react-avatar";

const ShowAllComments = () => {
  const [comments, setComments] = useState("");
  const [page, setPage] = useState(1);
  const [dataLength, setDataLength] = useState("");
  const [totalPages, setTotalPages] = useState(0);
  const token = localStorage.getItem("token").slice(1, -1);
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  async function fetchData() {
    await axios
      .get("http://localhost:8080/api/blog//show-all-comments", config)
      .then((res) => {
        // const comments = res.data.blogs.map((blogs) => blogs.comment);
        setComments(res.data.comment);

        // console.log(allComments, "allComments");
        console.log(res.data.comment, "res.data.blogs");
        // console.log(uniqueComments, "uniqueComments");
      })
      .catch((error) => {
        console.log(error);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    async function fetchData() {
      await axios
        .get(`http://localhost:8080/api/blog/show-posts?page=${page}`)
        .then((res) => {
          setPosts(res.data.blogs);
          setDataLength(res.data.BlogCount);
          setTotalPages(Math.ceil(res.data.BlogCount / 7));
        });
    }
    fetchData();
  }, [page]);

  function handlePaginationClick(newPage) {
    window.scrollTo(0, 0);
    setPage(newPage);
  }

  const paginationButtons = [];

  for (let i = 0; i <= totalPages + 1; i++) {
    if (i === 0) {
      paginationButtons.push(
        <button
          key={"first"}
          className={`btn btn-secondary mx-3 px-4 ${
            page === 1 ? "disabled" : ""
          }`}
          onClick={() => {
            handlePaginationClick(1);
          }}
          disabled={page === 1}
        >
          <i className="fa-solid fa-less-than"></i>
        </button>
      );
    } else if (i === totalPages + 1) {
      paginationButtons.push(
        <button
          key={"last"}
          className={`btn btn-secondary mx-3 px-4 ${
            page === totalPages ? "disabled" : ""
          }`}
          onClick={() => {
            handlePaginationClick(totalPages);
          }}
          disabled={page === totalPages}
        >
          <i className="fa-solid fa-greater-than"></i>
        </button>
      );
    } else {
      paginationButtons.push(
        <button
          key={i}
          className={`btn btn-secondary mx-3 px-4 ${
            i === page ? "active" : ""
          }`}
          onClick={() => {
            handlePaginationClick(i);
          }}
        >
          {i}
        </button>
      );
    }
  }

  return (
    <DashboardPage>
      <div className="display-5 border-bottom py-3">All Comments</div>

      {/* --------------Map Here---------------- */}

      {comments &&
        comments.map((comment, i) => {
          // console.log(comment, "comment");
          return (
            <>
              <div className="container" key={comment._id}>
                {comment.map((object, j) => {
                  console.log(object, "object");
                  const date = new Date(object?.createdAt);
                  const options = {
                    weekday: "long",
                    year: "numeric",
                    month: "long",
                    day: "numeric",
                  };
                  const formattedDate = date.toLocaleDateString(
                    "en-US",
                    options
                  );

                  return (
                    <>
                      <div className="container" key={object._id}>
                        <div className="card my-3">
                          <div className="card-header">
                            <div className="d-flex justify-content-between align-items-center">
                              <div>
                                <Avatar
                                  name={object.username}
                                  textSizeRatio={1.85}
                                  size="30"
                                  round={true}
                                />
                                <span className="mx-1 btn">
                                  {object.username}
                                </span>
                              </div>
                              <div className="">
                                <Link to="/blogPage" className="">
                                  <i className="fa-solid fa-pen-to-square fa-2x text-muted onHover-icons"></i>
                                </Link>
                                <div className="mx-3 d-inline"></div>
                                <Link to="/blogPage" className="">
                                  <i className="fa-solid fa-trash fa-2x text-muted onHover-icons"></i>
                                </Link>
                              </div>
                            </div>
                          </div>
                          <div className="card-body">
                            <blockquote className="blockquote mb-0">
                              <p>{object.comment}</p>
                              <footer className="blockquote-footer">
                                {formattedDate}
                                <span className="fw-bold mx-5">
                                  Comments On which Post
                                </span>
                              </footer>
                            </blockquote>
                          </div>
                        </div>
                      </div>
                    </>
                  );
                })}
              </div>
            </>
          );
        })}
    </DashboardPage>
  );
};

export default ShowAllComments;
