import React, { useEffect, useState } from "react";
import axios from "axios";

const LikeBlog = ({ blogId, likes }) => {
  //   const [likes, setLikes] = useState(0);
  const handleLike = () => {
    axios
      .post(`http://localhost:8080/api/blog/get-blog/like/${blogId}`)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <div className="d-flex mx-auto justify-content-evenly w-50">
        <h5 className="fs-3 m-3">Like a Post :</h5>
        <button className="btn btn-success m-2 " onClick={handleLike}>
          Like
        </button>
        <p className="fs-3 m-3">{likes}</p>
      </div>
      <hr className="w-50 mx-auto" />
    </>
  );
};

export default LikeBlog;
