import React from "react";
import { Link } from "react-router-dom";

const PopularPost = ({ popularPosts }) => {
  // const [posts, setPosts] = useState([]);

  // const fetchData = async () => {
  //   await axios.get("/api/blog/show-all-posts").then((res) => {
  //     setPosts(res.data.blogs);
  //   });
  // }

  // useEffect(() => {
  //   fetchData();
  // }, []);
  return (
    <>
      {popularPosts &&
        popularPosts.map((post, index) => {
          return (
            <div className="card p-1 mt-3" key={index}>
              <Link
                className="p-0 m-0 text-decoration-none text-dark"
                to={`/blogPage/${post._id}`}
              >
                <div className="row">
                  <div className="col-md-8">
                    <div className="card-body">
                      <h5 className="card-title">
                        {post.title.substring(0, 30)}
                      </h5>
                      <p className="card-text">
                        {post.content.substring(0, 50)}
                      </p>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <img
                      src={`http://localhost:8080/${post.path}`}
                      className="rounded-start m-0 p-0"
                      width={92}
                      height={95}
                      alt="..."
                    />
                  </div>
                </div>
              </Link>
            </div>
          );
        })}
    </>
  );
};

export default PopularPost;
