import React, { useRef, useState } from "react";
import JoditEditor, { Jodit } from "jodit-react";
import DashboardPage from "./DashboardPage";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";

/**
 * @param {Jodit} jodit
 */
function preparePaste(jodit) {
  jodit.e.on(
    "paste",
    (e) => {
      if (confirm("Change pasted content?")) {
        jodit.e.stopPropagation("paste");
        jodit.s.insertHTML(
          Jodit.modules.Helpers.getDataTransfer(e)
            .getData(Jodit.constants.TEXT_HTML)
            .replace(/a/g, "b")
        );
        return false;
      }
    },
    { top: true }
  );
}
Jodit.plugins.add("preparePaste", preparePaste);

const CreateNewPost = () => {
  const editor = useRef(null);
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const [title, setTitle] = useState("");
  const [file, setFile] = useState("");
  const config = {
    placeholder: "Post Content Type Here ...",
  };
  // console.log(editor);
  // console.log(title);
  // console.log(content);
  // console.log(file);
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("content", content);
      formData.append("file", file);

      const res = await axios.post("/api/blog/create-blog", formData);
      if (res.data.success === true) {
        toast.success("Post Created Successfully");
        navigate("/show-all-posts");
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };
  return (
    <DashboardPage>
      <div className="container-fluid">
        <div className="row mt-5">
          <div className="w-100">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title display-5 text-center">
                  Create New Post
                </h5>
                <p className="card-text pt-3">
                  <input
                    type="text"
                    className="form-control fs-3"
                    // placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </p>
                <p className="card-text py-3">
                  <input
                    type="file"
                    className="form-control fs-3"
                    onChange={(e) => setFile(e.target.files[0])}
                  />
                </p>
                <div className="card-text">
                  <JoditEditor
                    ref={editor}
                    value={content}
                    config={config}
                    className="form-control fs-3"
                    tabIndex={1} // tabIndex of textarea
                    onBlur={(newContent) => setContent(newContent)} // preferred to use only this option to update the content for performance reasons
                    onChange={(newContent) => {}}
                    style={{ minHeight: "300px" }}
                  />
                </div>
                <p className="card-text pt-3 ">
                  <button
                    onClick={handleSubmit}
                    className=" form-control btn btn-secondary fs-4"
                  >
                    Create Post
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </DashboardPage>
  );
};

export default CreateNewPost;
