import React from "react";
import { Link } from "react-router-dom";
import DisplayData from "../pages/Author/dangerouslySetInnerHTML";

const FeatureCard = ({ featurePost }) => {
  const date = new Date(featurePost?.createdAt);
  const options = {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };
  const formattedDate = date.toLocaleDateString("en-US", options);

  function extractPlainTextFromHtml(html) {
    const parser = new DOMParser();
    const doc = parser.parseFromString(html, "text/html");
    return doc.body.textContent || "";
  }

  // Example usage:
  const postContent = featurePost.content;
  const plainContent = extractPlainTextFromHtml(postContent);
  const shortContent = plainContent.substring(0, 250);

  const postTitle = featurePost.title;
  const plainTitle = extractPlainTextFromHtml(postTitle);
  const shortTitle = plainTitle.substring(0, 50);

  return (
    <>
      <div className="container-fluid">
        <div
          className="row p-md-5 rounded"
          style={{ backgroundColor: "#f4f0ec " }}
        >
          <div className="col-md-6 ">
            <h1 className="display-5 font-italic py-3">
              {/* Why Strategic Alignment in IT is important for Return on
              Investment in Start-Ups and SMEs alike */}
              <DisplayData data={featurePost.title} />
              {/* {featurePost.title} */}
            </h1>
            <p className="lead my-3 fs-2 ">
              {/* COMMENTS As the digital age we live in, keeps evolving and
              revolutionizing every aspect of our lives, these new inventions
              and digitization has also changed the present Business landscape.
              Thus, the need for Strategic IT Alignment becomes more prominent
              than ever. In the unlikely event that you have never stumbled upon
              this concept, */}
              <DisplayData data={featurePost.content} />
              {/* {featurePost.content} */}
            </p>
            <p className="lead my-5 d-flex justify-content-center">
              <Link
                to={`/blogPage/${featurePost._id}`}
                className="btn btn-primary btn-lg font-weight-bold p-3"
              >
                Continue reading...
              </Link>
            </p>
          </div>
          <div className="col-md-5">
            <img
              src={`http://localhost:8080/${featurePost.path}`}
              className="img-fluid rounded "
              alt="..."
            />
          </div>
        </div>
      </div>
    </>
  );
};

export default FeatureCard;
