import React, { useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import PopularPost from "../../components/PopularPost.js";
import Layout from "../../components/layout/Layout.js";

setError(error.message);
useEffect(() => {
  if (error) {
    const timeoutId = setTimeout(() => {
      setError("");
    }, 3000);

    return () => clearTimeout(timeoutId);
  }
}, [error]);

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [answer, setAnswer] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "name":
        setName(value);
        break;
      case "email":
        setEmail(value);
        break;
      case "password":
        setPassword(value);
        break;
      case "phone":
        setPhone(value);
        break;
      case "address":
        setAddress(value);
        break;
      case "answer":
        setAnswer(value);
        break;
      default:
        break;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/api/auth/register", {
        name,
        email,
        password,
        phone,
        address,
        answer,
      });
      if (res.data.success === true) {
        toast.success("Register Successfully");
        navigate("/login");
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };
  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-6 card offset-md-1 my-5 px-5 bg-light">
            <h1 className="text-center display-4 mt-5">Registeration </h1>
            <form className="" onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="fs-3 p-1">Name</label>
                <input
                  value={name}
                  onChange={handleChange}
                  type="text"
                  className="form-control form-control-lg"
                  name="name"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-3 p-1">Email</label>
                <input
                  value={email}
                  onChange={handleChange}
                  type="email"
                  className="form-control form-control-lg"
                  name="email"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-3 p-1">Password</label>
                <input
                  value={password}
                  onChange={handleChange}
                  type="password"
                  className="form-control form-control-lg"
                  name="password"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-3 p-1">Phone</label>
                <input
                  value={phone}
                  onChange={handleChange}
                  type="number"
                  className="form-control form-control-lg"
                  name="phone"
                  required
                />
              </div>
              <div className="form-group">
                <label className="fs-3 p-1">Address</label>
                <input
                  value={address}
                  onChange={handleChange}
                  type="text"
                  className="form-control form-control-lg"
                  name="address"
                  required
                />
              </div>{" "}
              <div className="form-group">
                <label className="fs-3 p-1">What is your favourite Book</label>
                <input
                  value={answer}
                  onChange={handleChange}
                  type="text"
                  className="form-control form-control-lg"
                  name="answer"
                  required
                />
              </div>
              {error && (
                <div className="form-group mx-auto text-danger">{error}</div>
              )}
              <div className="form-group mt-5">
                <button
                  type="submit"
                  className="form-control form-control-lg btn btn-secondary btn-lg btn-block p-2 my-3"
                >
                  Register
                </button>
              </div>
            </form>
          </div>
          <div className="col-md-4 my-5 mx-auto">
            <h4 className="display-5 mx-2 p-2">Popular Posts</h4>

            <PopularPost />
          </div>
          <hr className="w-50 mx-auto my-5 text-dark" />
        </div>
      </div>
    </Layout>
  );
};

export default Register;
