import React from "react";
import { Link } from "react-router-dom";
import DisplayData from "../pages/Author/dangerouslySetInnerHTML";

const Card = ({ posts }) => {
  return (
    <>
      {posts &&
        posts.map((post) => {
          const date = new Date(post?.createdAt);
          const options = {
            weekday: "long",
            year: "numeric",
            month: "long",
            day: "numeric",
          };
          const formattedDate = date.toLocaleDateString("en-US", options);

          function extractPlainTextFromHtml(html) {
            const parser = new DOMParser();
            const doc = parser.parseFromString(html, "text/html");
            return doc.body.textContent || "";
          }

          // Example usage:
          const postContent = post.content;
          const plainContent = extractPlainTextFromHtml(postContent);
          const shortContent = plainContent.substring(0, 60);

          const postTitle = post.title;
          const plainTitle = extractPlainTextFromHtml(postTitle);
          const shortTitle = plainTitle.substring(0, 25);

          return (
            <Link
              to={`/blogPage/${post._id}`}
              className="nav-link text-dark m-3 p-2 "
              style={{ textDecoration: "none" }}
              key={post._id}
            >
              <div className="card p-3 bg-light">
                <div className="row">
                  <div className="col-md-4">
                    <img
                      src={`/${post.path}`}
                      className="img-fluid rounded-start"
                      style={{ width: "auto", height: "180px" }}
                      alt="..."
                    />
                  </div>
                  <div className="col-md-8">
                    <div className="card-body">
                      <h5 className="card-title">
                        {shortTitle}
                        {/* <DisplayData data={post.title.substring(0, 70)} /> */}
                        {/* {post.title.substring(0, 50)}... */}
                      </h5>
                      <div className="card-text">
                        <DisplayData data={post.content.substring(3, 200)} />
                        {/* {post.content.substring(0, 200)}... */}
                      </div>

                      <p className="card-text">
                        <small className="text-muted">{formattedDate}</small>
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          );
        })}
    </>
  );
};

export default Card;
